/**
 * Set a mime list which are acceptable and compare it with request Content-Type.
 *
 * ```typescript
 *  @Controller('/mypath')
 *  export class MyCtrl {
 *
 *    @Get('/')
 *    @AcceptMime('application/json')
 *    public getResource(){}
 *  }
 * ```
 *
 * @param mimes
 * @returns {Function}
 * @decorator
 * @endpoint
 */
export declare function AcceptMime(...mimes: string[]): Function;
